function resizeButtons(size){
  for(var x=0;x<document.getElementById('buttons-container').children[0].children[0].children.length;x++){
    for(var y=0;y<document.getElementById('buttons-container').children[0].children[0].children[x].children.length;y++){
      document.getElementById('buttons-container').children[0].children[0].children[x].children[y].style.width = size
    }
  }

  for(var x=0;x<document.getElementById('generic-buttons-container').children[0].children.length;x++){
    for(var y=0;y<document.getElementById('generic-buttons-container').children[0].children[x].children.length;y++){
      document.getElementById('generic-buttons-container').children[0].children[x].children[y].style.width = size
    }
  }
}


